function dumentReady(fn) {
  if (document.readyState !== 'loading') {
    fn();
  } else if (document.addEventListener) {
    document.addEventListener('DOMContentLoaded', fn);
  } else {
    document.attachEvent('onreadystatechange', function() {
      if (document.readyState !== 'loading') {
        fn();
      }
    });
  }
}

function addEventListener(el, eventName, handler) {
  if (el.addEventListener) {
    el.addEventListener(eventName, handler);
  } else {
    el.attachEvent('on' + eventName, function(){
      handler.call(el);
    });
  }
}

function addClass(el, className) {
  if (el.classList) {
    el.classList.add(className);
  } else {
    el.className += ' ' + className;
  }
}

function removeClass(el, className) {
  if (el.classList) {
    el.classList.remove(className);
  } else {
    el.className = el.className.replace(new RegExp('(^|\\b)' + className.split(' ').join('|') + '(\\b|$)', 'gi'), ' ');
  }
}

// http://stackoverflow.com/questions/17452057/feature-detect-autoplay-html5-audio-audio-on-mobile-browsers
let AUTOPLAY = false; // A flag to be used in the anonymous function
(function() {
  // Audio file data URIs; MP3 + OGG
  let mp3 = 'data:audio/mpeg;base64,/+MYxAAAAANIAUAAAASEEB/jwOFM/0MM/90b/+RhST//w4NFwOjf///PZu////9lns5GFDv//l9GlUIEEIAAAgIg8Ir/JGq3/+MYxDsLIj5QMYcoAP0dv9HIjUcH//yYSg+CIbkGP//8w0bLVjUP///3Z0x5QCAv/yLjwtGKTEFNRTMuOTeqqqqqqqqqqqqq/+MYxEkNmdJkUYc4AKqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqqq';
  let ogg = 'data:audio/ogg;base64,';

  try {
    let audio = new Audio(); // Construct new Audio object
    let src = audio.canPlayType('audio/ogg') ? ogg : mp3; // Ternary statement determining the value of the src variable, which will be either ogg or mp3.
    audio.autoplay = true; // Set autoplay to true

    // This event will only be triggered if setting the autoplay attribute to true works.
    addEventListener(audio, 'play', function() {
      AUTOPLAY = true; // Set the global flag to true, indicating we have support.
    });

    audio.src = src; // Set the audio objects src to the value of the src variable.
  } catch (e) {
    // console.log('[AUTOPLAY-ERROR]', e);
    // This means we were unsuccessful - this browser doesn't support the autoplay attribute.
  }
})(); // Invoke anonymous function.

// main
function initPlayer() {
  let player = document.getElementsByClassName('js-player')[0];
  let playBtn = document.getElementsByClassName('js-play-btn')[0];
  let singerEl = document.getElementsByClassName('js-singer')[0];
  let songEl = document.getElementsByClassName('js-song')[0];
  let oldSong;
  let oldSinger;


  window.newSongData = function newSongData(data) {
    if (oldSinger !== data[0].artist) {
      singerEl.innerHTML = data[0].artist;
      oldSinger = data[0].artist;
    }

    if (oldSong !== data[0].title) {
      songEl.innerHTML = data[0].title;
      oldSong = data[0].title;
    }
  };


  function createJsTag() {
    let old = window.document.getElementById('js-data-tag');
    old && document.body.removeChild(old);
    let script   = document.createElement('script');
    script.src   = 'https://jsonp.afeld.me/?callback=newSongData&url=http://mjoy.ua/radio/station/urban-space-radio/playlist.json';
    script.id  = 'js-data-tag';
    document.body.appendChild(script);
  }
  createJsTag();
  setInterval(createJsTag, 5000);

  setTimeout(function() {
    if (!AUTOPLAY) {
      removeClass(playBtn, 'state-paying');
    }
  }, 500);


  addEventListener(playBtn, 'click', function() {
    if (player.paused) {
      player.play();
      addClass(playBtn, 'state-paying');
    } else {
      player.pause();
      removeClass(playBtn, 'state-paying');
    }
  });
}

dumentReady(initPlayer);
